import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {fetchPhoneById, addPhoneToBasket} from 'actions'
import {getPhoneById} from 'selectors'
import BasketCart from 'components/basketCart'

class Phone extends Component {
    state = {
        phone: null,
        loading: true,
        error: null,
    };
  componentDidMount() {
      const { match } = this.props;
      const phoneId = match.params.id;
      fetch('/api/products/get/${productId}')
        .then(response => {
          if (!response.ok) {
              throw new Error('Network response was not ok');
          }
          return response.json();
      })
          .then(data => this.setState({ phone: data, loading: false }))
          .catch(error => this.setState({ error, loading: false }));
  }


  renderContent() {
      const { phone, loading, error } = this.state;

      if (loading) {
          return <div>Loading...</div>;
      }

      if (error) {
          return <div>Error: {error.message}</div>;
      }

      if (!phone) {
          return <div>No phone found</div>;
      }

      return (
          <Layout>
              <div className='phone-detail'>
                  <h2>{phone.name}</h2>
                  <img className='img-thumbnail' src={phone.image} alt={phone.name} />
                  <h4 className='pull-right'>${phone.price}</h4>
                  <p>{phone.description}</p>
                  <Link to='/phones' className='btn btn-default'>Back to Phones</Link>
              </div>
          </Layout>
      );
  }


  renderSidebar() {
    const {phone, addPhoneToBasket} = this.state

    return (
      <div>
        <p className='lead'>Quick shop</p>
        <BasketCart/>
        <div className='form-group'>
          <h1>{phone.name}</h1>
          <h2>${phone.price}</h2>
        </div>
        <Link to='/' className='btn btn-info btn-block'>
          Back to store
        </Link>
        <button
          type='button'
          className='btn btn-success btn-block'
          onClick={() => addPhoneToBasket(phone.id)}
        >
          Add to cart
        </button>
      </div>
    )
  }

  render() {
    return (
      <div className='view-container'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-9'>
              {this.state.phone && this.renderContent()}
            </div>
            <div className='col-md-3'>
              {this.state.phone && this.renderSidebar()}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getPhoneById(state, state.phonePage.id)
  }
}

const mapDispatchToProps = {
  fetchPhoneById,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Phone)